var fs = require('fs');

module.exports = {

  init: function(callback) {
    if (!callback) {
      throw Error("callback is required!");
    }

    var _buffer = "";
    var inputStream = fs.createReadStream('input.txt');

    inputStream.on('data', function(data) {
        _buffer += data;
    });

    inputStream.on('end', function() {
      callback(_buffer);
    });
  }
};