module.exports = {

  init: function(callback) {
    if (!callback) {
      throw Error("callback is required!");
    }

    var _input = "";

    process.stdin.setEncoding('utf8');

    process.stdin.on('readable', function() {
      var chunk = process.stdin.read();
      if (chunk !== null) {
        _input += chunk;
      }
    });

    process.stdin.on('end', function() {
      console.log();
      callback(_input);
    });
  }
};